sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	data.problem= [];
var gr = new GlideRecord('problem');
gr.addQuery('assigned_to', gs.getUserID());
gr.query();
while(gr.next())
{
var problem_data={};

problem_data.number= gr.getValue("number");
problem_data.assigned_to= gr.getDisplayValue("assigned_to");
problem_data.short_description= gr.getValue("short_description");

data.problem.push(problem_data);

}

})();
